import react, { useState, useEffect } from "react";
import gql from "graphql-tag";
import { useQuery, Query } from "react-apollo";
import _ from "lodash";
import getCustomAttributes from '../utils/getCustomAttributes';

const GET_ORDERS = gql`
  {
    shop {
      id
      name
    }
    orders(first: 10, query: "fulfillment_status:unshipped") {
      edges {
        node {
          id
          name
          displayFulfillmentStatus
          note
          createdAt
          lineItems(first: 10) {
            edges {
              node {
                title
                quantity
              }
            }
          }
          customer {
            id
            firstName
            lastName
            email
            phone
          }
          customAttributes {
            key
            value
          }
        }
      }
    }
  }
`;

const Orders = () => {
  // Apollo data
  const { data } = useQuery(GET_ORDERS)

  // Local State
  const [orderedData, setOrderedData] = useState([]);
  const [method, setMethod] = useState("");

  // const addDateTime = (data) => {
  //   const dateTimeArray = [];

  //   qlData.map(edge => {
  //     const deliveryDate = getDeliveryDates(edge);
  //     const deliveryTime = getDeliveryTimes(edge);

  //     if(deliveryTime !== undefined) {
  //       const processTimesArray = deliveryTime.split(' ');
  //       const deliveryWindowParse = processTimesArray.slice(0, 2)
  //       const deliveryDueTime = deliveryWindowParse.join(' ')

  //       dateTimeArray.push(new Date(`${deliveryDate} ${deliveryDueTime}`).toISOString())
  //     }
  //     // console.log(dateTimeArray)
  //     setDataArray(...dataArray, [dataArray, qlData.concat(dateTimeArray)])
  //   })
  // }

  // console.log("DataArray", dataArray)

  return (
    <>
      <div>
        <h1>Orders</h1>
        <div className={"w-full flex"}>
          <button
            className={"btn btn-gray mr-2"}
            onClick={() => setMethod("pickup")}
          >
            Pickups
          </button>
          <button
            className={"btn btn-gray mr-2"}
            onClick={() => setMethod("delivery")}
          >
            Deliveries
          </button>
          <button
            className={"btn btn-gray"}
            onClick={() => setMethod("shipping")}
          >
            To Ship
          </button>
        </div>
      </div>
        {data !== undefined && data.orders.edges.map(
          (edge) =>
            // Show only the data associated with the Checkout Method
            getCheckoutMethod(edge) === method && (
              <div key={edge.node.name} className="border p-2">
                <h2>Order Name: {getCustomAttributes.getOrderName(edge)}</h2>
                <h4>Status: {getCustomAttributes.getFulFillmentStatus(edge)}</h4>
                <h2>Method: {getCustomAttributes.getCheckoutMethod(edge)}</h2>
                <h3>Customer Name: {getCustomAttributes.getCustomerFullName(edge)}</h3>
                <h3>Customer Email: {getCustomAttributes.getCustomerEmail(edge)}</h3>
                {edge.node.note && (
                  <h4>Customer Note: {getCustomAttributes.getCustomerNote(edge)}</h4>
                )}
                <h4>Time Created: {getCustomAttributes.getOrderCreationTime(edge)}</h4>
              </div>
            )
          )}
    </>
  );
};

export default Orders;
