import React, { useState } from "react";
import SelectStatus from "./SelectStatus";
// import _ from "lodash";
import {
  getCheckoutMethod,
  getDeliveryDates,
  getDeliveryTimes,
  getPickupTimes,
  getOrderName,
  getFulFillmentStatus,
  getCustomerFullName,
  getCustomerEmail,
  getCustomerNote,
  getOrderCreationTime,
} from "../utils/getCustomAttributes";

// const ORDER_UPDATE = gql`
