import react, { useState } from "react";
// import _ from "lodash";
import {
  getCheckoutMethod,
  getDeliveryDates,
  getDeliveryTimes,
  getPickupTimes,
  getOrderName,
  getFulFillmentStatus,
  getCustomerFullName,
  getCustomerEmail,
  getCustomerNote,
  getOrderCreationTime,
} from "../utils/getCustomAttributes";

// const ORDER_UPDATE = gql`
//   mutation orderUpdate($input: OrderInput!) {
//     orderUpdate(input: $input) {
//       order {
//         id
//       }
//       userErrors {
//         field
//         message
//       }
//     }
//   }
// `;

const Orders = (props) => {
  const { edge } = props.orderData
  console.log(orderData);
  const [method, setMethod] = useState("");
  const [orderStatus, setOrderStatus] = useState("");

  const handleStatus = (e) => {
    e.preventDefault();
    setOrderStatus(e.target.value);
  };

  return (
    <>
      <div>
        <h1>Orders</h1>
        <div className={"w-full flex"}>
          <button
            className={"btn btn-gray mr-2"}
            onClick={() => setMethod("pickup")}
          >
            Pickups
          </button>
          <button
            className={"btn btn-gray mr-2"}
            onClick={() => setMethod("delivery")}
          >
            Deliveries
          </button>
          <button
            className={"btn btn-gray"}
            onClick={() => setMethod("shipping")}
          >
            To Ship
          </button>
        </div>
      </div>
      {getCheckoutMethod(edge) === method && (
        <div
          className="border p-2 w-1/3 flex content-center"
        >
          <div className="flex w-2/3 p-2 flex-col">
            <h2>Order Name: {getOrderName(edge)}</h2>
            <h4>Status: {getFulFillmentStatus(edge)}</h4>
            <h2>Method: {getCheckoutMethod(edge)}</h2>
            <h3>Customer Name: {getCustomerFullName(edge)}</h3>
            <h3>Customer Email: {getCustomerEmail(edge)}</h3>
            {edge.node.note && <h4>Customer Note: {getCustomerNote(edge)}</h4>}
            <h4>Due Date: {getDeliveryDates(edge)}</h4>
            <h4>Due Time: {getDeliveryTimes(edge)}</h4>
          </div>
          <div>
            <label for="order-status-select">Update Status:</label>

            <select
              name="status"
              value={orderStatus}
              onChange={(e) => handleStatus(e)}
            >
              <option value="">--Status--</option>
              <option value="null">Cancelled</option>
              <option value="prep">In Prep</option>
              <option value="ready">Ready</option>
              <option value="notified">Customer Notified</option>
            </select>
          </div>
        </div>
      )}
      );
    </>
  );
};

export default Orders;

