import react, { useState, useEffect } from "react";
import gql from "graphql-tag";
import { Query } from "react-apollo";
import _ from "lodash";

const GET_ORDERS = gql`
  {
    shop {
      id
      name
    }
    orders(first: 10, query: "fulfillment_status:unshipped") {
      edges {
        node {
          id
          name
          displayFulfillmentStatus
          note
          createdAt
          lineItems(first: 10) {
            edges {
              node {
                title
                quantity
              }
            }
          }
          customer {
            id
            firstName
            lastName
            email
            phone
          }
          customAttributes {
            key
            value
          }
        }
      }
    }
  }
`;

const getCheckoutMethod = (edge) => {
  return _.chain(edge.node.customAttributes)
    .find({ key: "Checkout-Method" })
    .get("value")
    .value();
};
const getDeliveryDates = (edge) => {
  return _.chain(edge.node.customAttributes)
    .find({ key: "Delivery-Date" })
    .get("value")
    .value();
};
const getPickupTimes = (edge) => {
  return _.chain(edge.node.customAttributes)
    .find({ key: "Pickup-Time" })
    .get("value")
    .value();
};
const getDeliveryTimes = (edge) => {
  return _.chain(edge.node.customAttributes)
    .find({ key: "Delivery-Time" })
    .get("value")
    .value();
};
const getOrderName = (edge) => {
  return edge.node.name;
};
const getFulFillmentStatus = (edge) => {
  return edge.node.displayFulfillmentStatus;
};
const getCustomerFullName = (edge) => {
  return `${edge.node.customer.firstName} ${edge.node.customer.lastName}`;
};
const getCustomerEmail = (edge) => {
  return edge.node.customer.email;
};
const getCustomerNote = (edge) => {
  return edge.node.customer.note;
};
const getOrderCreationTime = (edge) => {
  return edge.node.createdAt;
};

const Orders = () => {

  const { data } = useQuery(GET_ORDERS)
  console.log(data)

  // const [dataArray, setDataArray] = useState([]);
  // const [orderedData, setOrderedData] = useState([]);
  const [method, setMethod] = useState("");

  // const addDateTime = (qlData) => {
  //   const dateTimeArray = [];

  //   qlData.map(edge => {
  //     const deliveryDate = getDeliveryDates(edge);
  //     const deliveryTime = getDeliveryTimes(edge);

  //     if(deliveryTime !== undefined) {
  //       const processTimesArray = deliveryTime.split(' ');
  //       const deliveryWindowParse = processTimesArray.slice(0, 2)
  //       const deliveryDueTime = deliveryWindowParse.join(' ')

  //       dateTimeArray.push(new Date(`${deliveryDate} ${deliveryDueTime}`).toISOString())
  //     }
  //     // console.log(dateTimeArray)
  //     setDataArray(...dataArray, [dataArray, qlData.concat(dateTimeArray)])
  //   })
  // }

  // console.log("DataArray", dataArray)

  return (
    <>
      <div>
        <h1>Orders</h1>
        <div className={"w-full flex"}>
          <button
            className={"btn btn-gray mr-2"}
            onClick={() => setMethod("pickup")}
          >
            Pickups
          </button>
          <button
            className={"btn btn-gray mr-2"}
            onClick={() => setMethod("delivery")}
          >
            Deliveries
          </button>
          <button
            className={"btn btn-gray"}
            onClick={() => setMethod("shipping")}
          >
            To Ship
          </button>
        </div>
      </div>
      <Query query={GET_ORDERS}>
        {({ data, loading, error }) => {
          if (loading) return <div>Loading…</div>;
          if (error) return <div>{error.message}</div>;

          const qlData = data.orders.edges;
          // setDataArray(qlData);
          // addDateTime(qlData);

          return qlData.map(
            (edge) =>
              // Show only the data associated with the Checkout Method
              getCheckoutMethod(edge) === method && (
                <div key={edge.node.name} className="border p-2">
                  <h2>Order Name: {getOrderName(edge)}</h2>
                  <h4>Status: {getFulFillmentStatus(edge)}</h4>
                  <h2>Method: {getCheckoutMethod(edge)}</h2>
                  <h3>Customer Name: {getCustomerFullName(edge)}</h3>
                  <h3>Customer Email: {getCustomerEmail(edge)}</h3>
                  {edge.node.note && (
                    <h4>Customer Note: {getCustomerNote(edge)}</h4>
                  )}
                  <h4>Time Created: {getOrderCreationTime(edge)}</h4>
                </div>
              )
          );
        }}
      </Query>
    </>
  );
};

export default Orders;
